    { value: "aries", label: "‚ôà Áâ°ÁæäÂ∫ß" },
    { value: "taurus", label: "‚ôâ ÈáëÁâõÂ∫ß" },
    { value: "gemini", label: "‚ôä ÈõôÂ≠êÂ∫ß" },
