// Ê™îÊ°à: src/components/FortuneTeller.jsx
import React, { useState, useCallback } from 'react';
import './FortuneTeller.css';

// --- ÈùúÊÖãË≥áÊñô ---
const ZODIAC_OPTIONS = [
    { value: "aries", label: "‚ôà Áâ°ÁæäÂ∫ß" },
    { value: "taurus", label: "‚ôâ ÈáëÁâõÂ∫ß" },
    { value: "gemini", label: "‚ôä ÈõôÂ≠êÂ∫ß" },
    { value: "cancer", label: "‚ôã Â∑®ËüπÂ∫ß" },
    { value: "leo", label: "‚ôå ÁçÖÂ≠êÂ∫ß" },
    { value: "virgo", label: "‚ôç ËôïÂ•≥Â∫ß" },
    { value: "libra", label: "‚ôé Â§©Áß§Â∫ß" },
    { value: "scorpio", label: "‚ôè Â§©Ë†çÂ∫ß" },
    { value: "sagittarius", label: "‚ôê Â∞ÑÊâãÂ∫ß" },
    { value: "capricorn", label: "‚ôë Êë©ÁæØÂ∫ß" },
    { value: "aquarius", label: "‚ôí Ê∞¥Áì∂Â∫ß" },
    { value: "pisces", label: "‚ôì ÈõôÈ≠öÂ∫ß" },
];

// --- ‰∏ªÁµÑ‰ª∂ ---
const FortuneTeller = () => {
    const [activeTab, setActiveTab] = useState('constellation');
    const [selectedZodiac, setSelectedZodiac] = useState(ZODIAC_OPTIONS[0].value);
    const [fortuneResult, setFortuneResult] = useState({ title: 'Ë´ãÈÅ∏ÊìáÊÇ®ÁöÑÊòüÂ∫ß‰∏¶ÈªûÊìä„ÄåÊü•ÁúãÈÅãÂã¢„Äç‰ª•ÈñãÂßã„ÄÇ', content: '' });
    const [isLoading, setIsLoading] = useState(false);

    // Â°îÁæÖÁâåÁãÄÊÖã
    const [tarotCardRevealed, setTarotCardRevealed] = useState(false);
    const [tarotCardImg, setTarotCardImg] = useState('');
    const [tarotResult, setTarotResult] = useState({ title: 'Â°îÁæÖÁâåÁµêÊûúÂ∞áÂú®Ê≠§ËôïÊè≠Á§∫„ÄÇ', meaning: '', cardName: '' });

    // --- ÊòüÂ∫ßÈÅãÂã¢ ---
    const handleShowFortune = useCallback(async () => {
        setIsLoading(true);
        setFortuneResult({ title: 'üîÆ Ê≠£Âú®ËÅØÁπ´ÂëΩÈÅã‰πãËº™ÔºåË´ãÁ®çÂÄô...', content: '' });
        const apiUrl = `http://localhost:4000/api/fortune?zodiac=${selectedZodiac}`;

        try {
            const response = await fetch(apiUrl);
            if (!response.ok) throw new Error(`‰º∫ÊúçÂô®ÈåØË™§: ${response.status}`);
            const data = await response.json();
            const selectedLabel = ZODIAC_OPTIONS.find(opt => opt.value === selectedZodiac)?.label || 'ÊòüÂ∫ß';

            setFortuneResult({ 
                title: `${selectedLabel} ‰ªäÊó•ÈÅãÂã¢Ôºö`, 
                content: data.fortune 
            });

        } catch (error) {
            console.error(error);
            setFortuneResult({
                title: '‚ùå ÈÅãÂã¢ËÆÄÂèñÂ§±Êïó„ÄÇ',
                content: 'Ë´ãÁ¢∫Ë™ç API ‰º∫ÊúçÂô®ÊòØÂê¶ÂïüÂãï„ÄÇ'
            });
        } finally {
            setIsLoading(false);
        }
    }, [selectedZodiac]);

    // --- ÈáçÁΩÆÂ°îÁæÖ ---
    const resetTarot = () => {
        setTarotCardRevealed(false);
        setTarotCardImg('');
        setTarotResult({ title: 'Â°îÁæÖÁâåÁµêÊûúÂ∞áÂú®Ê≠§ËôïÊè≠Á§∫„ÄÇ', meaning: '', cardName: '' });
    };

    // --- Â°îÁæÖÊäΩÁâå ---
    const handleDrawTarot = async () => {
        if (tarotCardRevealed) {
            resetTarot();
            return;
        }

        try {
            const response = await fetch('https://tarotapi.dev/api/cards/random');
            if (!response.ok) throw new Error(`Â°îÁæÖ API ÈåØË™§: ${response.status}`);
            const data = await response.json();

            setTarotCardImg(data.image); // API Â∑≤Êèê‰æõÂÆåÊï¥ÂúñÁâá URL
            setTarotCardRevealed(true);
            setTarotResult({
                title: `üîÆ ÊÅ≠ÂñúÊäΩÂà∞ „Äê${data.name}„Äë`,
                meaning: data.meaning_up || data.meaning_rev || 'ÁÑ°Ëß£Èáã',
                cardName: data.name
            });

        } catch (error) {
            console.error(error);
            setTarotResult({
                title: '‚ùå Â°îÁæÖÁâåÊäΩÂèñÂ§±Êïó',
                meaning: 'Ë´ãÊ™¢Êü•Á∂≤Ë∑ØÊàñ API ÊòØÂê¶ÂèØÁî®',
                cardName: ''
            });
        }
    };

    // --- ÂçÄÂ°äÊ∏≤Êüì ---
    const renderConstellationSection = () => (
        <section className="content-section active">
            <h2>‰ªäÊó•ÊòüÂ∫ßÈÅãÂã¢ ‚ú®</h2>
            <div className="zodiac-selector">
                <label htmlFor="zodiac-select">Ë´ãÈÅ∏ÊìáÊòüÂ∫ß:</label>
                <select
                    id="zodiac-select"
                    value={selectedZodiac}
                    onChange={(e) => setSelectedZodiac(e.target.value)}
                    disabled={isLoading}
                >
                    {ZODIAC_OPTIONS.map(opt => (
                        <option key={opt.value} value={opt.value}>{opt.label}</option>
                    ))}
                </select>
                <button 
                    id="show-fortune-btn"
                    onClick={handleShowFortune} 
                    disabled={isLoading}
                >
                    {isLoading ? 'ÈÅãÁÆó‰∏≠...' : 'Êü•ÁúãÈÅãÂã¢'}
                </button>
            </div>
            <div className="result-box">
                <h3>{fortuneResult.title}</h3>
                {fortuneResult.content && <p>{fortuneResult.content}</p>}
            </div>
        </section>
    );

    const renderTarotSection = () => (
        <section className="content-section">
            <h2>üîÆ Á∑ö‰∏äÂ°îÁæÖÊäΩÁâå</h2>
            <div className="tarot-area">
                <div
                    id="tarot-card"
                    className={tarotCardRevealed ? 'card-reveal' : ''}
                    onClick={handleDrawTarot}
                >
                    <div className="card-back">ÈªûÊìäÊäΩÁâå</div>
                    <div className="card-front">
                        {tarotCardImg && <img src={tarotCardImg} alt={tarotResult.cardName} />}
                        {!tarotCardImg && 'ÈªûÊìäÊäΩÁâå'}
                    </div>
                </div>
            </div>
            <button id="draw-tarot-btn" onClick={handleDrawTarot}>
                {tarotCardRevealed ? 'ÈáçÊñ∞ÊäΩÁâå' : 'ÊäΩÂèñ‰ªäÊó•Â°îÁæÖÁâå'}
            </button>
